import React, { Component } from 'react';
import './App.css';
import Counter from "./components/Counter/Counter"

class App extends Component {
  constructor() {
    super()

    this.state = {
      num1: 0,
      num2: 0,
      num3: 0
    }
  }

  increase() {
    this.setState.num1 += 1;
  }

  decrease() {
    this.setState.num1 -= 1;
  }

  increaseAll() {
    this.setState.num1 += 1;
    this.setState.num2 += 1;
    this.setState.num3 += 1;
  }

  decreaseAll() {
    this.setState.num1 -= 1;
    this.setState.num2 -= 1;
    this.setState.num3 -= 1;
  }

  render() {
    return (
      <div className="App">
        <button onClick={this.increase} className="Add">+</button>
        <h1>{this.state.num1}</h1>
        <button className="Subtract">-</button>

        <button className="Add">+</button>
        <h1>-5</h1>
        <button className="Subtract">-</button>

        <button className="Add">+</button>
        <h1>-1</h1>
        <button className="Subtract">-</button>

        <button>+ all</button>
        <button>- all</button>
      </div>
    );
  }
}

export default App;
