import React, { Component } from 'react';
import './App.css';
import Counter from "./components/Counter/Counter"

class App extends Component {
  constructor() {
    super()

    this.state = {
      num1: 0,
      num2: 10,
      num3: 20
    }
    this.increase = this.increase.bind(this)
    this.decrease = this.decrease.bind(this)
  }

  increase(evt) {
    switch (evt.target.id) {
      case 1:
        this.setState({num1: this.state.num1++});
        break;
      case 2:
        this.setState({num2: this.state.num2++}); 
        break;
      case 3:
        this.setState({num3: this.state.num3++});
        break;
    }
  }

  decrease() {
    this.setState({num1: this.state.num1--})
    console.log("Clicked")
    console.log(this.state.num1)
  }

  increaseAll() {
    this.setState.num1 += 1;
    this.setState.num2 += 1;
    this.setState.num3 += 1;
  }

  decreaseAll() {
    this.setState.num1 -= 1;
    this.setState.num2 -= 1;
    this.setState.num3 -= 1;
  }

  render() {
    return (
      <div className="App">
        <Counter 
        nums={this.state.num1} 
        increment={this.increase} 
        decrement={this.decrease}
        id={1}
        />
        <Counter 
        nums={this.state.num2} 
        increment={this.increase} 
        decrement={this.decrease}
        id={2}
        />
        <Counter 
        nums={this.state.num3} 
        increment={this.increase} 
        decrement={this.decrease}
        id={3}
        />

        <button>+ all</button>
        <button>- all</button>
      </div>
    );
  }
}

export default App;
