import React, {Component} from "react";

export default class Counter extends Component {


    render() {
        return (
            <div className="App">
                <button onClick={this.props.increase} className="Add">+</button>
                <h1>{this.props.num1}</h1>
                <button onClick={this.props.decrease} className="Subtract">-</button>
            </div>
        )
    }
}
