export default class Counter extends Component {
  constructor()  {
    super();
    this.state = {
      ctr: 0
    }
  }

  increment() {
    this.setState(state => ({ctr: state.ctr + 1}));
    this.props.onChange();
  }

  decrement() {
    this.setState(state => ({ctr: state.ctr - 1}));
    this.props.onChange();
  }

  render() {
    return (
      <div>
        <button onClick={() => this.decrement()}>Decrement</button>
        {this.state.ctr}
        <button onClick={() => this.increment()}>Increment</button>
      </div>
    );
  }
}
