import React, { Component } from 'react';
import './App.css';
import Counter from "./components/Counter/Counter"

class App extends Component {
  constructor() {
    super()

    this.state = {
      num1: 2,
      num2: 0,
      num3: 0
    }
    this.increase = this.increase.bind(this)
    this.decrease = this.decrease.bind(this)
  }

  increase() {
    this.setState(
      {num1: this.state.num1++})

    console.log("Clicked")
    console.log(this.state.num1)
  }

  decrease() {
    this.setState(
      {num1: this.state.num1--})
    console.log("Clicked")
    console.log(this.state.num1)
  }

  increaseAll() {
    this.setState.num1 += 1;
    this.setState.num2 += 1;
    this.setState.num3 += 1;
  }

  decreaseAll() {
    this.setState.num1 -= 1;
    this.setState.num2 -= 1;
    this.setState.num3 -= 1;
  }

  render() {
    return (
      <div className="App">
        <Counter nums={this.state.num1}/>
        <Counter nums={this.state.num2}/>
        <Counter nums={this.state.num3}/>

        <button>+ all</button>
        <button>- all</button>
      </div>
    );
  }
}

export default App;
