import React, { Component } from 'react';
import './App.css';
import transformers from './transformers/transformer-data';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      transformer: transformers
    }
  }

  //step 1:
  renderNames = () =>  {
    this.state.transformer.map(i => i.name);

    //map through this.state.transformers
    //return an array of jsx elements
    //that contains the transformer names
  }


  //step 2:
  renderAutobots() {
    let a = this.state.transformer.filter(i => i.team === "autobot");
    console.log(a);
    //Call filter on transformers and filter for just autobots.
    //Call map on the filtered array and return an array
    //of jsx elements that contains just the autobots names.
    //Call the renderAutobots function in the render method of App.js
    //to display all autobots.
  }

  //Proceed with steps 3 and 4.


  render() {
    this.renderNames();
    return (
      <div className="App">
        <h1>All transformers:</h1>
        <h2>All Autobots:</h2>
      </div>
    );
  }
}

export default App;
